{		$numero=$ligne['numero'];		$liste=$ligne['liste'];		$designation_text=text2js($ligne['designation']);		$designation=$ligne['designation'];		$type=$ligne['type'];			$type_image=$ligne['type_image'];					$taille=$ligne['taille'];		$prix=$ligne['prix'];		$id=$ligne['id'];		$designation_courte=$ligne['designation_courte'];		$couleur=$ligne['couleur'];		$marque=$ligne['marque'];		$code=$ligne['code'];		$bourse=$ligne['bourse'];		$message="Voulez-vous supprimer $liste-$numéro $designation ?";		echo "<tr bgcolor=$bgcolor>		<td> $code $bourse-$numero  </td>		<td> $designation_courte </td>		<td> $designation  </td>		<td> $couleur </td>		<td> $marque </td>		<td><img src=\"$type_image\"></td>		<td> $taille </td> 		<td> $prix €</td>		</tr>";				$counter++;		if (($counter%2) == 0):			$bgcolor="#FFFFFF";		else:			$bgcolor="#EEEEEE";				endif;	}		$query="select sum(prix),count(*) 	from pm_articles  where vente=$vente 	";	$result=exec_sql($query,$connection);	while ($ligne=fetch_row($result))	{		echo "<tr>		<td colspan=7> TOTAL </td> 		<td> $ligne[0] €</td>		</tr>";				echo "<tr>		<td colspan=6>  </td> 		<td colspan=2> $ligne[1] articles</td>		</tr>";				}	echo "</table>";?></body></html>